<script>
import MDBDraggable from "./components/MDBDraggable.vue";
import MDBSortable from "./components/MDBSortable.vue";
import MDBSortableItem from "./components/MDBSortableItem.vue";
import Docs from "@/utils/docs";
import { ref } from "vue";

export default {
  name: "App",
  components: {
    MDBDraggable,
    MDBSortableItem,
    MDBSortable,
    Docs,
  },
  setup() {
    const sortableMultiTables1 = ref(null);
    const sortableMultiTables2 = ref(null);

    const sortableCopy1 = ref(null);
    const sortableCopy2 = ref(null);

    const sortableCondition1 = ref(null);
    const sortableCondition2 = ref(null);

    const sortableDisabled1 = ref(null);
    const sortableDisabled2 = ref(null);

    const sortableMulti1 = ref(null);
    const sortableMulti2 = ref(null);

    const accessOddNumbers = (value) => {
      return parseInt(value) % 2;
    };

    return {
      sortableMultiTables1,
      sortableMultiTables2,
      sortableCopy1,
      sortableCopy2,
      sortableCondition1,
      sortableCondition2,
      sortableDisabled1,
      sortableDisabled2,
      sortableMulti1,
      sortableMulti2,
      accessOddNumbers,
    };
  },
};
</script>

