<template>
  <component
    :is="tag"
    :class="className"
    :data-mdb-disabled="disabled ? true : null"
    :data-mdb-value="value ?? null"
    :data-mdb-drag-handle="dragHandle ?? null"
    ref="sortableItemRef"
  >
    <slot />
  </component>
</template>

<script>
import { computed, ref } from "vue";

export default {
  name: "MDBSortableItem",
  props: {
    tag: {
      type: String,
      default: "div",
    },
    disabled: Boolean,
    dragHandle: String,
    classes: String,
    itemClass: {
      type: String,
      default: "sortable-item",
    },
    value: [String, Number],
  },
  setup(props) {
    const sortableItemRef = ref(null);
    const className = computed(() => [props.itemClass, props.classes]);

    return {
      className,
      sortableItemRef,
    };
  },
};
</script>
